import { config } from '@gluestack-ui/config';
import { useAuth } from '../Context/AuthContext';
import { useState } from 'react';
import axios from 'axios';
import {Alert} from 'react-native';
import { useNavigation } from '@react-navigation/stack';
import { Box, GluestackUIProvider, Text, Center, VStack, Heading, Input, InputField, Button, ButtonText, Icon, Image} from '@gluestack-ui/themed';



function Login  (){

  const {login} = useAuth();
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const navigation = useNavigation();

    const handleSubmit = async () => {
        try {
          // Verificar si el email y la contraseña están presentes
          if (!email || !password) {
            Alert.alert('Error', 'Por favor, ingresa tu email y contraseña.');
            return;
          }
          const response = await axios.post('http://127.0.0.1:8000/api/login', {
            email,
            password,
          });
          login(response.data)
          console.log('Respuesta del backend:', response.data);
            Alert.alert('Registro exitoso', 'Usuario registrado correctamente.');

      
         
          navigation.navigate('Profile');
      
        } catch (error) {
          // Manejar errores de autenticación
          console.error('Error de autenticación:', error);
      
          // Puedes mostrar un mensaje de error al usuario
          Alert.alert('Error', 'Credenciales inválidas. Por favor, inténtalo de nuevo.');
        }
      };

    return(
        <>
        
        <Center backgroundColor={'$black'}>
          <Box p='$5' maxWidth='$96' borderWidth='$1' borderColor='$backgroundLight300' borderRadius='$lg' $dark-borderColor="$backgroundDark700">
            <VStack space='xs' pb='$4' alignItems='center'>
              <Heading lineHeight={32} color={'$white'}>
                Welcome Login
              </Heading>

              <Image
                width={200}
                height={32}
                resizeMode= 'stretch'
                //size="md"
                source={{
                    uri: "https://raw.githubusercontent.com/JorgeIvan172/Imagenes/master/MULTI%20-%20PROJETS/InnovaDesk%20Logo%20Blanco.png",
                }}
                />

            </VStack>
            <VStack space='xl' py='$2'>
              <Input>
                <InputField
                  py='$2'
                  color={'$white'}
                  placeholder="Email Adress"
                />
              </Input>
              <Input>
                <InputField
                  color={'$white'}
                  py='$2'
                  placeholder="Password"
                />
              </Input>
            </VStack>
            <VStack space='lg' pt='$4' >
              <Button
                size='sm'
                backgroundColor={'#B35340'}
              >
                <ButtonText>
                    Access
                </ButtonText>
              </Button>
              <Box flexDirection='row'>
                <Button variant='link' p='$0' size='sm'>
                  {/* ArrowLeftIcon is imported from 'lucide-react-native' */}
                  <Icon
                    size='md'
                    mr='$1'
                    //as={ArrowLeftIcon}
                  />
                  <ButtonText color={'#B35340'}
                  >
                    Back
                  </ButtonText>
                </Button>
              </Box>
            </VStack>
          </Box>
        </Center>
      
        </>
    );
}

export default Login;
