import React from 'react';
import { useCart } from '../Context/CartContext';
import {
  Box,
  VStack,
  Heading,
  Button,
  ButtonText,
  Image,
  Text,
} from '@gluestack-ui/themed';

const CartComponent = () => {
  const { cart, removeFromCart } = useCart();

  const handleRemoveFromCart = (productId: string) => {
    removeFromCart(productId);
  };

  return (
    <Box flex={1} backgroundColor={'$black'} paddingTop={'$32'} paddingBottom={'$32'}>
      <VStack space='xs' pb='$4' alignItems='center'>
        <Heading lineHeight={32} color={'$white'}>
          Cart Items
        </Heading>
        {cart.map((item) => (
          <Box
            backgroundColor={'#B35340'}
            padding={'$3'}
            margin={'$2'}
            borderWidth={'$2'}
            borderColor='#ccc'
            borderRadius={10}
            flexDirection='row'
            maxWidth='$96'
            key={item.product.id}
          >
            <Image
              source={{ uri: item.product.img }}
              width={50}
              height={50}
              borderRadius={5}
              marginRight={10}
            />
            <Text color='white' marginTop={'$3'}>
              {item.product.name} - ${item.product.price} (Quantity: {item.quantity})
              <Button
                size="xs"
                marginLeft={'$1.5'}
                marginTop={'$1.5'}
                action="negative"
                isDisabled={false}
                isFocusVisible={false}
                onPress={() => handleRemoveFromCart(item.product.id)}
              >
                <ButtonText>Delete</ButtonText>
              </Button>
            </Text>
          </Box>
        ))}
      </VStack>
    </Box>
  );
};

export default CartComponent;
