import React from 'react'
import {
    HStack,
    Avatar, 
    AvatarFallbackText,
    AvatarImage,
    Center,
    Text,
    Input,
    InputField
  } from '@gluestack-ui/themed';
  import { useAuth } from '../Context/AuthContext';

  export default function Profile() {
    const { userData } = useAuth();
  
    return (
      <Center backgroundColor={'$black'} paddingTop={'$32'} paddingBottom={'$32'}>
        <HStack space="md" h="100%" justifyContent="center" alignItems="center">
          <Avatar size="2xl">
            <AvatarFallbackText>Your Profile</AvatarFallbackText>
            <AvatarImage
              source={{
                uri:'https://raw.githubusercontent.com/JorgeIvan172/Imagenes/master/MULTI%20-%20PROJETS/Avatar.png',
              }}
            />
          </Avatar>
        </HStack>
        {userData && userData.length > 0 ? (
        <>
          <Text size="2xl" color={"white"}></Text>
          <Text size="2xl" color={"white"}>{userData[0].email}</Text>
          <Text size="2xl" color={"white"}>{userData[0].phone}</Text>
          <Input>
                <InputField
                  py='$2'
                  color={'$white'}
                  placeholder="Email Adress"
                  value={email}
                  onChangeText={(text) => setEmail(text)}
                  onBlur={() => console.log('El campo de entrada ha perdido el foco')}
                />
                  {userData[0].name}
              </Input>
        </>
      ) : (
        <Text>User not logged in.</Text>
      )}
      </Center>
    );
  }
