import React from 'react'
import { View } from 'react-native';
import { useNavigation } from '@react-navigation/native';

import {
    Box,
    VStack,
    Heading,
    Input,
    InputField,
    Button,
    ButtonText,
    Image,
    Center,
    Text
  } from '@gluestack-ui/themed';

  
  
  const OneProduct = ({ route }) => {
    const navigation = useNavigation();
    const { id, name, price, img } = route.params.product;
  
    const handleBackPress = () => {
      navigation.goBack(); // Volver a la pantalla anterior
    };
  

    return (
      <>
      <Center>
      
        <Image    
        source={{ uri: item.product.img }}
        width={80}
        height={80}></Image>
        <Text>ID: {id}</Text>
        <Text>Name: {name}</Text>
        <Text>Price: ${price}</Text>
        {/* Otros detalles del producto... */}
      



      <Box flexDirection='row'>
            <Button variant='link' p='$0' size='sm' onPress={handleBackPress}>
                  {/* ArrowLeftIcon is imported from 'lucide-react-native' */}
              <ButtonText color={'#B35340'}
            >
            Back
            </ButtonText>
          </Button>
      </Box>
      </Center>
      </>
    );
  };
  
  export default OneProduct;
