// ProductComponent.tsx
import React from 'react';
import { View} from 'react-native';
import {
  Box,
  VStack,
  Heading,
  Input,
  InputField,
  Button,
  ButtonText,
  Image,
  Center,
  Text
} from '@gluestack-ui/themed';

interface ProductProps {
  product: {
    id: string;
    name: string;
    price: number;
    img: string
    // Otros detalles del producto...
  };
}

const OneProduct = () => {
  console.log('Presionaste el Producto');
};

const ProductComponent: React.FC<ProductProps> = ({ product }) => {
  const { id, name, price, img } = product;

  return (
    <>
    <TouchableOpacity onPress={handlePress}></TouchableOpacity>
    <Box backgroundColor={'#B35340'} key={id} padding={'$3'} margin={'$2'} borderWidth={'$2'} borderColor='#ccc' borderRadius={10} >
      
      <View  style={{ flexDirection: 'row' }}>
      <Image
      width={100}
      height={100}
      resizeMode= 'stretch'
      borderRadius={5}
        source={{ uri: img }}
      />
      
      <View style={{ marginLeft: 10 }}>
      <Text size="xl" color='black' fontWeight='bold'>{name}</Text>
      <Text size="md" color='black' fontWeight='bold'>${price}</Text>

      <Button marginTop={'$1'} size='sm' backgroundColor={'#233342'} width={'$32'}>
      <ButtonText>Add to Car +</ButtonText>
      </Button>

      </View>
      </View>

      {/* Otros detalles del producto... */}
    </Box>
    </>
  );
};



export default ProductComponent;
