// Otro componente
import React from 'react';
import { useCart } from '../Context/CartContext'; // Ajusta la ruta correcta
import {
  Box,
  VStack,
  Heading,
  Image,
  Text,
  Button,
  ButtonText,
} from '@gluestack-ui/themed';

const AnotherComponent = () => {
  const { cart, removeFromCart } = useCart();

  const handleRemoveFromCart = (productId) => {
    removeFromCart(productId);
  };

  return (
    <Box flex={1} backgroundColor={'$black'} paddingTop={'$32'} paddingBottom={'$32'}>
      <VStack space='xs' pb='$4' alignItems='center'>
        <Heading lineHeight={32} color={'$white'}>
          Cart Items
        </Heading>

        {cart.map((item) => (
          <Box
            key={item.id}
            backgroundColor={'#B35340'}
            padding={'$3'}
            margin={'$2'}
            borderWidth={'$2'}
            borderColor='#ccc'
            borderRadius={10}
            flexDirection='row'
            justifyContent='space-between'
            alignItems='center'
            width='100%'
          >
            <Box flexDirection='row' alignItems='center'>
              <Image
                source={{ uri: item.img }}
                width={50}
                height={50}
                borderRadius={5}
                marginRight={10}
              />
              <Text color='white' size='md' fontWeight='bold'>
                {item.name}
              </Text>
            </Box>

            <Box flexDirection='row' alignItems='center'>
              <Text color='white' size='md' marginRight={'$1.5'}>
                ${item.price}
              </Text>
              <Button
                size='xs'
                action='negative'
                isDisabled={false}
                isFocusVisible={false}
                onPress={() => handleRemoveFromCart(item.id)}
              >
                <ButtonText>Delete</ButtonText>
              </Button>
            </Box>
          </Box>
        ))}
      </VStack>
    </Box>
  );
};

export default AnotherComponent;
